import base64

# Reemplaza el contenido Base64 con el proporcionado
base64_content = ""

# Decodifica el contenido Base64
image_data = base64.b64decode(base64_content)

# Especifica el nombre del archivo de imagen
file_path = "imagen_decodificada.jpg"

# Guarda la imagen decodificada en un archivo JPG
with open(file_path, "wb") as image_file:
    image_file.write(image_data)

print(f"Imagen guardada en {file_path}")
